import React, {useState, useEffect} from 'react'
import Datepicker from "./Datepicker.js"
import axios from "axios"
import { useNavigate } from 'react-router-dom'

const TodoList = () => {
  const [todoTitle, setTodoTitle] = useState("");
  const [todoDescription, setTodoDescription] = useState("");
  const [todoDeadline, setTodoDeadline] = useState("");
  const [todoUserid, setTodoUserid] = useState("");
  const [todoStatus, setTodoStatus] = useState(0);
  const navigate = useNavigate();

  const saveTodo = async (e) =>{
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/todos',{
        todoTitle, 
        todoDescription, 
        todoDeadline,
        todoStatus,
        todoUserid
      });
      // navigate("/");
      console.log("todoTitle:", todoTitle);
      console.log("todoDescription:", todoDescription);
      console.log("todoDeadline:", todoDeadline);
      console.log("todoStatus:", todoStatus);
      console.log("todoUserid:", todoUserid);

    } catch (error) {
      console.log(error);
    }
  }

  return (
    <section className="vh-100" style={{backgroundColor: '#e2d5de'}}>
        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-xl-10">
              <div className="card" style={{borderRadius: '15px'}}>
                <div className="card-body p-5">
                <h4 className="mb-3">Create New Todos</h4>

                  <form onSubmit={saveTodo}>
                    <div className="form-group mb-3">
                      <label htmlFor="titleInput" className="form-label">Todo Title</label>
                      <input type="text" className="form-control" id="titleInput" value={todoTitle} onChange={(e)=> setTodoTitle(e.target.value)} placeholder="E.g. completing my mathemathics tasks" />
                    </div>
                    <div className="form-group mb-3">
                      <label htmlFor="descriptionInput" className="form-label">Todo Description</label>
                      <textarea className="form-control" id="descriptionInput" value={todoDescription} onChange={(e)=> setTodoDescription(e.target.value)} rows={3} />
                    </div>
                    {/* <div className="form-group mb-3">
                      <label htmlFor="deadlineInput" className="form-label">Todo Deadline</label>
                    </div>
                    <div className="form-group mb-3">
                      <Datepicker value={todoDeadline} onChange={(e)=> setTodoDeadline(e.target.value)} className="form-control"/>
                    </div> */}
                    <div className="form-group mb-3">
                      <label htmlFor="useridInput" className="form-label">User ID</label>
                      <input type="text" value={todoUserid} onChange={(e)=> setTodoUserid(e.target.value)} className="form-control" placeholder="E.g. 0 for Krisna Wandhana" />
                    </div>
                    <div className="form-group mb-3">
                      <button type="submit" className="btn btn-primary">Create Todos!</button>
                    </div>
                  </form>
                  <ul className="list-group mb-0">
                    {/* <li className="list-group-item d-flex d-flex justify-content-between align-items-center border-start-0 border-top-0 border-end-0 border-bottom rounded-0 mb-2">
                      <div className="d-flex align-items-center">
                        <input className="form-check-input me-2" type="checkbox" defaultValue aria-label="..." defaultChecked />
                        <s>Dapibus ac facilisis in</s>
                      </div>
                      <a href="#!" data-mdb-toggle="tooltip" title="Remove item">
                        <i className="fas fa-times text-primary" />
                      </a>
                    </li>
                    <li className="list-group-item d-flex d-flex justify-content-between align-items-center border-start-0 border-top-0 border-end-0 border-bottom rounded-0 mb-2">
                      <div className="d-flex align-items-center">
                        <input className="form-check-input me-2" type="checkbox" defaultValue aria-label="..." />
                        Morbi leo risus
                      </div>
                      <a href="#!" data-mdb-toggle="tooltip" title="Remove item">
                        <i className="fas fa-times text-primary" />
                      </a>
                    </li> */}
                  </ul>
                </div>
                
              </div>
            </div>
          </div>
        </div>
      </section>
  )
}

export default TodoList
